import React, { useEffect } from 'react';
import './App.css';

const App = () => {
  useEffect(() => {
    const boxes = document.querySelectorAll('.box');
    boxes.forEach((box) => {
      box.addEventListener('mouseover', () => {
        box.style.backgroundColor = `hsl(${Math.random() * 360}, 70%, 70%)`;
      });
      box.addEventListener('mouseout', () => {
        box.style.backgroundColor = 'rgba(255, 255, 255, 0.2)';
      });
    });
  }, []);

  return (
    <div>
      {/* Animierter Hintergrund */}
      <div className="background"></div>

      {/* Inhalt */}
      <div className="content">
        <h1>Willkommen auf unserer IT-Website</h1>
        <p>Erleben Sie hochwertige Animationen und Designs.</p>

        {/* Animierte Boxen */}
        <div className="box-container">
          <div className="box"></div>
          <div className="box"></div>
          <div className="box"></div>
          <div className="box"></div>
        </div>
      </div>

      {/* Footer */}
      <footer>
        <p>Â© 2025 IT-Website. Alle Rechte vorbehalten.</p>
      </footer>
    </div>
  );
};

export default App;
