import React, { useEffect } from 'react';

const App = () => {
  useEffect(() => {
    // JavaScript für Animationen
    const boxes = document.querySelectorAll('.box');
    boxes.forEach((box) => {
      box.addEventListener('mouseover', () => {
        box.style.backgroundColor = `hsl(${Math.random() * 360}, 70%, 70%)`;
      });
      box.addEventListener('mouseout', () => {
        box.style.backgroundColor = 'rgba(255, 255, 255, 0.2)';
      });
    });
  }, []);

  return (
    <div>
      {/* Inline-CSS für den animierten Hintergrund */}
      <style>{`
        body {
          margin: 0;
          padding: 0;
          font-family: Arial, sans-serif;
          overflow: hidden;
        }

        .background {
          position: fixed;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          background: linear-gradient(45deg, #1d3557, #457b9d, #a8dadc, #f1faee);
          background-size: 400% 400%;
          animation: gradientAnimation 10s ease infinite;
        }

        @keyframes gradientAnimation {
          0% {
            background-position: 0% 50%;
          }
          50% {
            background-position: 100% 50%;
          }
          100% {
            background-position: 0% 50%;
          }
        }

        .content {
          position: relative;
          z-index: 1;
          color: white;
          text-align: center;
          padding: 20px;
        }

        h1 {
          font-size: 3rem;
          margin-top: 20%;
          animation: fadeIn 2s ease-in-out;
        }

        p {
          font-size: 1.5rem;
          animation: fadeIn 3s ease-in-out;
        }

        @keyframes fadeIn {
          0% {
            opacity: 0;
          }
          100% {
            opacity: 1;
          }
        }

        .box-container {
          display: flex;
          justify-content: center;
          align-items: center;
          flex-wrap: wrap;
          margin-top: 50px;
        }

        .box {
          width: 100px;
          height: 100px;
          background-color: rgba(255, 255, 255, 0.2);
          margin: 10px;
          border-radius: 10px;
          animation: bounce 2s infinite;
        }

        @keyframes bounce {
          0%, 100% {
            transform: translateY(0);
          }
          50% {
            transform: translateY(-20px);
          }
        }

        footer {
          position: fixed;
          bottom: 0;
          width: 100%;
          text-align: center;
          padding: 10px;
          background-color: rgba(0, 0, 0, 0.5);
          color: white;
        }
      `}</style>

      {/* HTML-Inhalt */}
      <div className="background"></div>
      <div className="content">
        <h1>Willkommen auf unserer IT-Website</h1>
        <p>Erleben Sie hochwertige Animationen und Designs.</p>
        <div className="box-container">
          <div className="box"></div>
          <div className="box"></div>
          <div className="box"></div>
          <div className="box"></div>
        </div>
      </div>
      <footer>
        <p>© 2025 IT-Website. Alle Rechte vorbehalten.</p>
      </footer>
    </div>
  );
};

export default App;
