import React, { useEffect } from 'react';
import ExampleComponent from './components/ExampleComponent';
import AdminLogin from './components/AdminLogin';
import AdminDashboard from './components/AdminDashboard';
import './App.css';

function App() {
  useEffect(() => {
    // JavaScript für die Animation der Boxen
    const boxes = document.querySelectorAll('.box');
    boxes.forEach((box) => {
      box.addEventListener('mouseover', () => {
        box.style.backgroundColor = `hsl(${Math.random() * 360}, 70%, 70%)`;
      });
      box.addEventListener('mouseout', () => {
        box.style.backgroundColor = 'rgba(255, 255, 255, 0.2)';
      });
    });

    // Navigation-Button-Interaktion
    const navButton = document.querySelector('.navbar-btn');
    const navWrapper = document.querySelector('.navbar-wrapper');
    navButton.addEventListener('click', () => {
      navWrapper.classList.toggle('change');
    });
  }, []);

  return (
    <div className="App">
      {/* Animierter Hintergrund */}
      <div className="background"></div>

      {/* Runde Navigationsleiste */}
      <div className="navbar-wrapper">
        <button className="navbar-btn">+</button>
        <div className="navbar-menu">
          <a href="#home" className="nav-item"><i className="fas fa-home"></i></a>
          <a href="#search" className="nav-item"><i className="fas fa-search"></i></a>
          <a href="#add" className="nav-item"><i className="fas fa-plus"></i></a>
          <a href="#settings" className="nav-item"><i className="fas fa-cog"></i></a>
        </div>
      </div>

      {/* Mann mit Anzug und Laptop */}
      <img
        src="/man-with-laptop.png" // Pfad zum Bild
        alt="Mann mit Laptop"
        className="man-image"
      />

      {/* Inhalt */}
      <header className="App-header">
        <h1 className="text-4xl font-bold mb-4">Willkommen auf unserer IT-Webseite</h1>
        <ExampleComponent />
        <AdminLogin />
        <AdminDashboard />

        {/* Animierte Boxen */}
        <div className="box-container">
          <div className="box"></div>
          <div className="box"></div>
          <div className="box"></div>
          <div className="box"></div>
        </div>
      </header>

      {/* Zusätzlicher Inhalt für Scrollen */}
      <div style={{ height: '200vh', backgroundColor: '#f4f4f4', padding: '20px' }}>
        <h2>Zusätzlicher Inhalt</h2>
        <p>Scrollen Sie nach unten, um mehr zu sehen!</p>
      </div>

      {/* Noch mehr Inhalt */}
      <div style={{ height: '200vh', backgroundColor: '#e0e0e0', padding: '20px' }}>
        <h2>Mehr Inhalt</h2>
        <p>Hier ist noch mehr Inhalt, um das Scrollen zu verlängern.</p>
      </div>
    </div>
  );
}

export default App;
